import React from 'react'
import CoinGecko from 'coingecko-api'
import { useEffect } from 'react'
import { useMemo } from 'react'

export const Main: React.FC = () => {
  const CoinGeckoClient = useMemo((): any => new CoinGecko(), [])

  useEffect((): void => {
    ;(async (): Promise<void> => {
      // const dsds = await CoinGeckoClient.coins.fetchMarketChart('bitcoin', { days: 28 })
      // console.log(dsds.data)

      const { data } = await CoinGeckoClient.coins.fetchMarketChartRange('bitcoin', {
        from: 1483228800,
        to: 1631720497456,
      })

      const arrPrices: [] = data.prices
      const arrNeedPrices: number[] = []
      const arrNumberOfCoins: number[] = []

      arrPrices.forEach((elem: number[]) => {
        const [timestamp, price] = elem
        const day: number = +new Date(timestamp).toLocaleString('en', { day: 'numeric' })

        if (day === 28) arrNeedPrices.push(+price.toFixed(0))
      })

      let sumOfAllPrices: number = 0

      arrNeedPrices.forEach((elem: number) => {
        const boughtCoins: number = 100 / elem
        arrNumberOfCoins.push(boughtCoins)
        // sumOfAllPrices += elem
      })

      const averagePrice: number = +(sumOfAllPrices / arrNeedPrices.length).toFixed(0)
      const investedFunds: number = arrNeedPrices.length * 100

      console.log(arrNumberOfCoins)

      // const [timestamp, price] = data.prices[0]
      // const date: string = new Date(timestamp).toLocaleDateString()

      // console.log(`Date: ${date}, price: ${price}`)
    })()
  }, [CoinGeckoClient])

  return <div>Calc</div>
}
