import React from 'react'
import CoinGecko from 'coingecko-api'
import { useEffect } from 'react'
import { useMemo } from 'react'

export const Main: React.FC = () => {
  const CoinGeckoClient = useMemo((): any => new CoinGecko(), [])

  useEffect((): void => {
    ;(async (): Promise<void> => {
      const { data } = await CoinGeckoClient.coins.fetchMarketChartRange(
        'bitcoin',
        { from: 1483228800, to: 1514764800 }
      )

      const arrPrices: any = data.prices
      let sumOfAllPrices: number = 0

      arrPrices.forEach((elem: any) => {
        const [timestamp, price] = elem

        sumOfAllPrices += price
      })

      const averagePrice: number = sumOfAllPrices / arrPrices.length

      // const [timestamp, price] = data.prices[0]
      // const date: string = new Date(timestamp).toLocaleDateString()

      // console.log(`Date: ${date}, price: ${price}`)
    })()
  }, [CoinGeckoClient])

  return <div>Calc</div>
}
